plugins {    java    application    `kotlin-dsl`    `maven-publish`}dependencies {    implementation(project(":asserts-counter-agent"))}gradlePlugin {    plugins {	create("asserts-counter-plugin") {	    id = "asserts-counter-plugin"	    implementationClass = "com.shivohamai.testing.tools.junit5.assertscounter.plugin.gradle.ACPlugin"	    displayName = "Asserts Counter Gradle Plugin"	    description = "Automatically applies the asserts-counter java agent to the test task"	}    }}