val kotlinVersion = "2.2.20-RC"plugins {    id("org.gradle.kotlin.kotlin-dsl") version "6.2.0"}kotlin {    sourceSets["main"].kotlin {	srcDir("src")	include("**/*.kt")    }    sourceSets["main"].resources.srcDirs("src/res")}dependencies {    implementation("com.github.ben-manes:gradle-versions-plugin:0.52.0")    implementation("se.patrikerdes:gradle-use-latest-versions-plugin:0.2.19")    implementation("org.codehaus.groovy:groovy-all:3.0.25")}gradlePlugin {    plugins {	create(project.name) {	    id = project.name	    version = project.version.toString()	    implementationClass = "com.shivohamai.testing.tools.junit5.assertscounter.plugin.gradle.ACPlugin"	    displayName = "Asserts Counter Gradle Plugin"	    description = "Automatically applies the asserts-counter java agent to the test task"	}    }}afterEvaluate {    require(project.version != Project.DEFAULT_VERSION) {	"Project version must be set to avoid unspecified manifest entry"    }    tasks.jar {	manifest {	    attributes["Implementation-Version"] = project.version	}    }}